// 1. Add type annotations to this object
// person should have: name (string), age (number), isActive (boolean), hobbies (string array)
const person = {
    name: "Alice",
    age: 30,
    isActive: true,
    hobbies: ["reading", "coding", "gaming"]
};

// 2. Add types to this function
// Takes a person object and returns a formatted string
function formatPerson(person) {
    return `${person.name} (${person.age}) - ${person.hobbies.join(", ")}`;
}

// 3. Add types to this function
// Takes an array of numbers and returns their sum
function calculateTotal(numbers) {
    return numbers.reduce((sum, n) => sum + n, 0);
}

