const api = new ApiClient('https://jsonplaceholder.typicode.com');

async function testApi() {
    try {
        // Test GET
        console.log('Testing GET...');
        const posts = await api.get('/posts?_limit=2');
        console.log('Got posts:', posts.length);

        // Test POST
        console.log('\nTesting POST...');
        const newPost = await api.post('/posts', {
            title: 'Test Post',
            body: 'This is a test',
            userId: 1
        });
        console.log('Created post:', newPost.id);

        // Test PUT
        console.log('\nTesting PUT...');
        const updatedPost = await api.put('/posts/1', {
            id: 1,
            title: 'Updated Title',
            body: 'Updated body',
            userId: 1
        });
        console.log('Updated post:', updatedPost.title);

        // Test DELETE
        console.log('\nTesting DELETE...');
        const deleted = await api.delete('/posts/1');
        console.log('Deleted:', deleted);

    } catch (error) {
        console.error('Error:', error.message);
