// Define the Describable trait here
// It should have:
// - describe(&self) -> String (required)
// - describe_short(&self) -> String (default: first 20 chars of describe)
trait Describable {
    todo!()
}

// Book struct: title, author, pages (u32)
struct Book {
    todo!()
}

// Implement Describable for Book
// describe() should return: "'<title>' by <author> (<pages> pages)"

// Movie struct: title, director, minutes (u32)
struct Movie {
    todo!()
}

// Implement Describable for Movie
// describe() should return: "'<title>' directed by <director> (<minutes> min)"

fn main() {
    let book = Book {
        title: String::from("The Rust Programming Language"),
        author: String::from("Steve Klabnik"),
        pages: 560,
    };

    let movie = Movie {
        title: String::from("Inception"),
        director: String::from("Christopher Nolan"),
        minutes: 148,
    };

    println!("Book: {}", book.describe());
    println!("Book short: {}", book.describe_short());
    println!("Movie: {}", movie.describe());
    println!("Movie short: {}", movie.describe_short());
}
